package com.xworkz.Inheritence.External;

public class Temple {

    public Temple() {
        System.out.println("Temple is the superclass");
    }

    public void openTemple() {
        System.out.println("Temple is open for devotees");
    }

    public void performPuja() {
        System.out.println("Temple performs daily puja and rituals");
    }

    public void offerPrasad() {
        System.out.println("Temple offers prasad to devotees");
    }

    public void ringBell() {
        System.out.println("Temple has bells ringing during prayers");
    }

    public void closeTemple() {
        System.out.println("Temple closes after prayer time");
    }
}
